import React, { useEffect, useState } from "react";

//Empty add form
const initialState = {
  name: "Nickname",
  appeared_healty: true,
  location: "Location Seen",
  email: "example@mail.com",
  date: new Date(),
};

function reducer(state, action) {
  switch (action.type) {
    case "editDate":
      return { ...state, date: action.value };

    case "editName":
      return { ...state, name: action.value };

    case "editLocation":
      return { ...state, location: action.value };

    case "editHealth":
      return { ...state, appeared_healthy: action.value };

    case "editEmail":
      return { ...state, email: action.value };
    case "wipe":
      return { ...initialState };
    default:
      return state;
  }
}

//ADD FORM, CHILD OF EVENTBOARD
function SightingAddForm(props) {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = state;
      //console.log("Attempting to post...", JSON.stringify(body)); // TEST

      const response = await fetch("http://localhost:3001/events", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      const update = await response.json();
      window.alert("Event submitted!");

      props.fetchData();
      dispatch({type: "wipe", value: {initialState}});

      window.location = "/";

    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <>
      <h2 className="tableTitle">Report Sighting</h2>
      <form
        id="sightingSubmission"
        action="#sightingSubmission"
        onSubmit={onSubmitForm}
      >
        <label for="Date">Date Seen:</label>
        <input
          id="Date"
          type="datetime-local"
          value={state.date}
          onChange={(e) => {
            dispatch({ type: "editDate", value: e.target.value });
          }}
        />

        <label for="Name">Name:</label>
        <input
          id="Name"
          type="text"
          value={state.name}
          onChange={(e) => {
            dispatch({ type: "editName", value: e.target.value });
          }}
        />

        <label for="Health"> Appeared Healthy? </label>
        <div
          id="Health"
          onChange={(e) => {
            dispatch({ type: "editHealth", value: e.target.value });
          }}
        >
          <input type="radio" value={true} name="gender" /> Yes
          <input type="radio" value={false} name="gender" /> No
        </div>

        <label for="Location">Location of Sighting:</label>
        <input
          id="Location"
          rows="10"
          cols="30"
          value={state.desc}
          onChange={(e) => {
            dispatch({ type: "editLocation", value: e.target.value });
          }}
        />

        <label for="Email">Your Email:</label>
        <input
          id="Email"
          type="text"
          value={state.Email}
          onChange={(e) => {
            dispatch({ type: "editEmail", value: e.target.value });
          }}
        />

        <input id="submitEvent" type="submit" />
      </form>
    </>
  );
}

export default SightingAddForm;
