import React, { useEffect, useState, useCallback } from "react";
import Sighting from "./Sighting";

function SightingsBoard() {
  const [tableData, setTable] = useState([]);
  const [needsUpdate, updateSwitch] = useState(false);
  var reducedTableData = [];

  const fetchData = async () => {
    try {
      let res = await fetch("http://localhost:9000/sightings");
      let sightingResults = await res.json();
      //console.log(sightingResults);

      setTable(sightingResults);
      groupedSightings();
    } catch (e) {
      alert("Error: Data not fetched!", e);
      console.log("Error: Data not fetched!");
    }
  };

  const groupedSightings = (origTable) => {
    let red
    function alreadyIncluded(id){
      let check = false;
      let i = 0;
      while(check === false && (i === 0 || i < reducedTableData.length) ){
      let arr = reducedTableData[i];
        if(arr[0].sighting_id === id){
          check = true;
        }
      }
      return check;
    }

    //reducedTableData = [[{},{},{}], [{}], [{}, {}],]
    origTable.forEach((sighting) => {

      //Groups sightings into array, pushes to reducedTableData
      if(!alreadyIncluded(sighting.sighting_id)){
        reducedTableData.push( 
          origTable.filter(subSighting => subSighting.sighting_id === sighting.sighting_id)
        );
      };
    });

    //reduce reducedTableData's subarrays
    function reduceFurther(sightingObj, indexObj){

      sightingObj.individuals.push(
        { nickname:indexObj.nickname, 
          common_name: indexObj.common_name,
          appeared_healthy:indexObj.appeared_healthy  }
      );

      return sightingObj;
    };

    reducedTableData = reducedTableData.map((sightings) => {
      if(sightings.length > 1){
        let initialObject = {
          date_time: sightings[0].date_time,
          location: sightings[0].location,
          sighter_email: sightings[0].sighter_email,
          individuals: [
            { nickname: sightings[0].sightings[0],
              common_name: sightings[0].common_name,
              appeared_healthy: sightings[0].appeared_healthy }
          ]
        };

        sightings.reduce(reduceFurther(), initialObject);
      }

      return sightings;
    });

    console.log("LOOK HERE:", reducedTableData); //TEST
  };

  useEffect(() => {
    fetchData();
    updateSwitch(false);
  }, [tableData]);

  return (
    <div className="SightingsBoard">

        {reducedTableData ? (
          reducedTableData.map((item, index) => {
            return <Sighting 
                    key={index} 
                    date={item.date_time} 
                    individuals={item.individuals}
                    location={item.location} 
                    sighter_email={item.sighter_email} 

                    />;
          })
        ) : (
          <p>Something went wrong... :c</p>
          )}

    </div>
  );
}

export default SightingsBoard;
