import React from 'react';

function Sighting(props){
    let date = new Date(props.date);
    let dateString = date.toLocaleDateString("en-GB", {
      day: "numeric",
      month: "short",
      year: "numeric",
    });
    let time = date.toLocaleTimeString(undefined, {
      hour: "2-digit",
      minute: "2-digit",
    });

    return (
      <div className="sighting">
        <div className="sightingdate">
          <p>{dateString} {time}</p>
        </div>
        <div className="individuals">
          {
            props.individuals.map( (ind) => {
                return (
                    <li>
                    <div className="nickname">

                        <p>{ind.nickname}</p>
                    </div>
                    <div className="speciesCommonName">
                        <p>{ind.common_name}</p>    
                    </div>
                    <div className="healthstatus">
                        <p>{ind.appeared_healthy ? `healthy` : `unhealthy`}</p>
                    </div>
                    </li>
                )
                })
        }
        </div>
        <div className="location">
          <h3>At:</h3>
          <p>{props.location}</p>
        </div>
        <div className="sighter">
          <h3>By:</h3>
          <p>{props.sighter_email}</p>
        </div>
      </div>
    );
}

export default Sighting;
